	/**
	 * Selects a collection of PropertyFloorplan objects pre-filled with all related objects.
	 *
	 * @param      Criteria  $criteria
	 * @param      PropelPDO $con
	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of PropertyFloorplan objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAll(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		PropertyFloorplanPeer::addSelectColumns($criteria);
		$startcol2 = (PropertyFloorplanPeer::NUM_COLUMNS - PropertyFloorplanPeer::NUM_LAZY_LOAD_COLUMNS);

		PropertyPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (PropertyPeer::NUM_COLUMNS - PropertyPeer::NUM_LAZY_LOAD_COLUMNS);

		$criteria->addJoin(PropertyFloorplanPeer::PROPERTY_ID, PropertyPeer::ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BasePropertyFloorplanPeer', $criteria, $con);
		}

		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = PropertyFloorplanPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = PropertyFloorplanPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = PropertyFloorplanPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				PropertyFloorplanPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined Property rows

			$key2 = PropertyPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = PropertyPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = PropertyPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					PropertyPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (PropertyFloorplan) to the collection in $obj2 (Property)
				$obj2->addPropertyFloorplan($obj1);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

